Go<pre><code>func test1() {
    s1 := []int{1, 2}
    s2 := s1
    s2 = append(s2, 3)
    fmt.Printf("s2: %v\n", s2) // s2: [1 2 3]
    fmt.Printf("s1: %v\n", s1) // s1: [1 2]
    SliceRise(s1)
    SliceRise(s2)
    fmt.Printf("s2: %v\n", s2) // s2: [2 3 4]
    fmt.Printf("s1: %v\n", s1) // s1: [1 2]
}

func SliceRise(s []int) {
    s = append(s, 0)
    for i := range s {
        s[i] += 1
    }
}
</code></pre>

Go<pre><code>package main

import "fmt"

func main() {
    a := []int{1, 2, 3, 4}
    b := a[5:] // panic: runtime error: slice bounds out of range [5:4]
    fmt.Printf("a: %v\n", a)
    fmt.Printf("b: %v\n", b)

    inorder := []int{3}
    right_part_inorder := inorder[1:]
    fmt.Printf("inorder: %v\n", inorder)
    fmt.Printf("right_part_inorder: %v\n", right_part_inorder)
    // inorder: [3]
    // right_part_inorder: []
}
</code></pre>
